#! /bin/bash
for d in `ls --hide *.sh`; do echo "$d: `grep -r 'name=\"LabResult' $d | wc -l` Labs, `grep -r 'name=\"Problem' $d | wc -l` Problems, `grep -r 'name=\"Medication' $d | wc -l` Medications, `grep -r 'name=\"Fill\"' $d | wc -l` Fills, `grep -r 'name=\"Immunization' $d | wc -l` Immunizations, `grep -r 'name=\"Allergy\"' $d | wc -l` Allergies, `grep -r 'name=\"AllergyExclusion' $d | wc -l` Allergy Exclusions, `grep -r 'name=\"VitalSign' $d | wc -l` Vitals"; done